:root {
    --primary-background: #083c4a;
    --primary-foreground: #839496;
    --highlight-foreground: hsl(186, 8%, 68%);
}

html, iframe {
   background-color: var(--primary-background);
   color: var(--primary-foreground)
}

input, select, option {
   background-color: var(--primary-background);
   color: var(--primary-foreground)
}
a {
   background-color: inherit;
   color: var(--highlight-foreground);
   text-decoration: underline; 
   font-weight: bold;
}

a:visited {
   background-color: var(--primary-background);
   color: var(--highlight-foreground);
   text-decoration: underline; 
   font-weight: normal;
}

